# 6408

class Solution:
    def minKBitFlips(self, A: List[int], K: int) -> int:
        ans = 0
        n = len(A)
        s = [0 for _ in range(n+K+1)]
        cur = 0
        for i in range(n):
            cur ^= s[i]
            if A[i] ^ cur == 0:
                if i + K > n:
                    return -1
                ans += 1
                cur ^= 1
                s[i+K] = 1
        return ans
